
def read_substr(st, from_idx, to_idx):
	s = set()
	for i in range(from_idx, to_idx):
		s.add(st[i])
	return s

def find_first_unique_substr(s, min_uniq_char_len = 4):
	for init_idx in range(len(s)):
		end_idx = init_idx + min_uniq_char_len
		char_set = read_substr(s, init_idx, end_idx)
		if len(char_set) == min_uniq_char_len:
			return end_idx

	
s = ''
expected_result = 10

r = find_first_unique_substr(s, 14)
print(r)


